import React, { useState, useEffect, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Navigate } from "react-router-dom";
import "./smashboys.css";

function SmashBoys() {
  const canvas = useRef(null);

  useEffect(() => {
    //Setup canvas background
    const ctx = canvas.current.getContext("2d");

    const gravity = 0.2;

    // Sprite constructor
    class Sprite {
      constructor({ position, velocity }) {
        this.position = position;
        this.velocity = velocity;
        this.height = 150;
        this.lastkey = null;
        this.attackBox 
      }
      draw() {
        ctx.fillStyle = "red";
        ctx.fillRect(this.position.x, this.position.y, 50, this.height);
      }

      update() {
        this.draw();
        this.position.x += this.velocity.x;
        this.position.y += this.velocity.y;
        if (this.position.y + this.height + this.velocity.y >= 576) {
          this.velocity.y = 0;
        } else this.velocity.y += gravity;

        if (this.position.x + this.width + this.velocity.x <= 0) {
          this.velocity.x = 0;
        }
        // this.position.x += this.velocity.x;
      }
    }

    const player1 = new Sprite({
      position: {
        x: 0,
        y: 0,
      },
      velocity: { x: 0, y: 0 },
    });

    const player2 = new Sprite({
      position: {
        x: 400,
        y: 100,
      },
      velocity: { x: 0, y: 0 },
    });

    const renderCanvas = () => {
      ctx.fillStyle = "black";
      ctx.fillRect(0, 0, 1024, 576);
    };

    renderCanvas();

    const keys = {
      a: {
        pressed: false,
      },
      d: {
        pressed: false,
      },
      w: {
        pressed: false,
      },
      lArrow: {
        pressed: false,
      },
      rArrow: {
        pressed: false,
      },
      uArrow: {
        pressed: false,
      },
    };
    function animate() {
      window.requestAnimationFrame(animate);
      renderCanvas();
      player1.update();
      player2.update();

      if (player1.position.y + player1.height + player1.velocity.y >= 576)
        player1.velocity.x = 0;
      if (player2.position.y + player2.height + player2.velocity.y >= 576)
        player2.velocity.x = 0;

      if (keys.a.pressed && player1.lastkey === "a") {
        player1.velocity.x = -5;
      } else if (keys.d.pressed && player1.lastkey === "d") {
        player1.velocity.x = 5;
      }
      if (
        keys.w.pressed &&
        player1.position.y + player1.height + player1.velocity.y >= 576
      ) {
        player1.velocity.y = -10;
      }

      if (keys.lArrow.pressed) {
        player2.velocity.x = -5;
      } else if (keys.rArrow.pressed) {
        player2.velocity.x = 5;
      }
      if (
        keys.uArrow.pressed &&
        player2.position.y + player2.height + player2.velocity.y >= 576
      ) {
        player2.velocity.y = -10;
      }
    }

    animate();

    window.addEventListener("keydown", (e) => {
      console.log(e);
      switch (e.key) {
        //player1
        case "d":
          keys.d.pressed = true;
          player1.lastkey = "d";
          break;
        case "a":
          keys.a.pressed = true;
          player1.lastkey = "a";
          break;
        case "w":
          keys.w.pressed = true;
          break;

        //player2
        case "ArrowLeft":
          keys.lArrow.pressed = true;
          player2.lastkey = "lArrow";
          break;
        case "ArrowRight":
          keys.rArrow.pressed = true;
          player2.lastkey = "rArrow";
          break;
        case "ArrowUp":
          keys.uArrow.pressed = true;
          break;
      }
    });
    window.addEventListener("keyup", (e) => {
      switch (e.key) {
        case "d":
          keys.d.pressed = false;
          break;
        case "a":
          keys.a.pressed = false;
          break;
        case "w":
          keys.w.pressed = false;
          break;
        //player2
        case "ArrowLeft":
          keys.lArrow.pressed = false;

          break;
        case "ArrowRight":
          keys.rArrow.pressed = false;

          break;
        case "ArrowUp":
          keys.uArrow.pressed = false;
          break;
      }
    });
  }, [canvas]);

  return (
    <div className="canvasDiv">
      <canvas ref={canvas} width={1024} height={576} />
    </div>
  );
}
export default SmashBoys;
