import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Navigate } from "react-router-dom";
import "./pantry.css";
import { getPantriesByUID } from "../../store/pantry";
import { getItemsByPantryId } from "../../store/items";
function PantryPage() {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getPantriesByUID(1));
    dispatch(getItemsByPantryId(1));
  }, [dispatch]);
  const pantries = useSelector((state) => {
    return state?.pantry;
  });

  return (
    <div>
     pantries && <div className="pantryNavbar">
        {pantries.map((pantry) => pantry.name)}
      </div>
    </div>
  );
}
export default PantryPage;
