import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Navigate } from "react-router-dom";
import "./pantry.css";
import { getPantriesByUID } from "../../store/pantry";
import { getItemsByPantryId } from "../../store/items";
function PantryPage() {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getPantriesByUID(1));
    dispatch(getItemsByPantryId(1));
  }, [dispatch]);
  let [currentPantry, setCurrentPantry] = useState(0);
  let [currentPantryName, setCurrentPantryName] = useState("");
  const pantries = useSelector((state) => {
    return state?.pantry;
  });
  const currPantryItems = useSelector((state) => {
    return state?.items;
  });

  const handlePantryChoice = (pantryId) => {
    setCurrentPantry(pantryId);
  };

  return (
    <div>
      {pantries.length && (
        <div className="pantryNavbar">
          {pantries.map((pantry) => (
            <div
              className="singlePantryButton"
              onClick={() => {
                handlePantryChoice(pantry.id);
                setCurrentPantryName(pantry.name);
              }}
            >
              {pantry.name}
            </div>
          ))}
        </div>
      )}

      <div className="pantryItemsHolder">
        {currentPantry === 0 && (
          <div className="currPantryName">Select a pantry</div>
        )}
        {currentPantry != 0 && currPantryItems && (
          <div>
            <div className="currPantryName">{currentPantryName}</div>
            <div className="pantryItemsList">
              <div className="pantryItemsTitle">
                <div className="itemTitle">Name</div>
                <div className="itemTitle">Amount</div>
                <div className="itemTitle">Minimum</div>
                <div className="itemTitle">Category</div>
                <div className="itemTitle">Tracked</div>
              </div>
              {currPantryItems.map((item) => (
                <div className="indivItem">
                  <div className="itemName">{item.name}</div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
export default PantryPage;
